var binToDecimal = function binToDecimal(bin) {
  'use strict';

  if (typeof bin !== 'string') {
    bin = String(bin);
  }
  var l = bin.length;
  var dec = 0;
  for (var i = l - 1; i >= 0; --i) {
    if (bin[i] !== '1' && bin[i] !== '0') {
      throw new Error('binToDecimal can only convert binary numbers. ' + bin + ' is not valid');
    }
    var one_zer = parseInt(bin[i], 2);
    dec += one_zer * Math.pow(2, l - i - 1);
  }
  return dec;
};

module.exports = binToDecimal;